import React from "react"
import {useState, useEffect} from "react";
import {useDispatch, useSelector} from "react-redux"
import { Link } from "react-router-dom";
import {getVideoGames,sortByRating,sortAlphabetically} from "../../actions/index";
import Card from "../Card/Card";
import Paginado from "../Paginado/Paginado";

export default function Home (){
    const dispatch = useDispatch ()
    const allGames  = useSelector ((state) => state.games)


    const [currentPage,setCurrentPage]= useState(1);
    const [gamesPerPage,setGamesPerPage]=useState(15);
    const indexOfLastGame = currentPage * gamesPerPage;
    const indexOfFirstGame = indexOfLastGame - gamesPerPage;
    const currentGames = allGames.slice(indexOfFirstGame,indexOfLastGame)
    const [header, setHeader] = useState("Explore all the Games");
    const [order, setOrder] = useState("Watch games");

    const paginado = (pageNumber) => {
        setCurrentPage(pageNumber)
    }


    useEffect (()=>{
        dispatch(getVideoGames())
    },[dispatch])


    function handleClick (e){
        e.preventDefault();
        dispatch(getVideoGames())
        setCurrentPage(1)
    }

    function handleRatingSort(e) {
        e.preventDefault();
        dispatch(sortByRating(e.target.value));
        setCurrentPage(1);
        setOrder(`Sorted by: ${e.target.value}`);
        setHeader(`Sorted by: ${e.target.value}`);
      }

      function handleSort(e) {
        e.preventDefault();
        dispatch(sortAlphabetically(e.target.value));
        setCurrentPage(1);
        setOrder(`Sorted by: ${e.target.value}`);
        setHeader(`Sorted by: ${e.target.value}`); // se agrega estado local 'Sort' para que se pueda renderizar el ordenamiento desde la página 1
      }










    return (
        <div className="header">

            <Link to="/videogame">Crear videoJuego</Link>
            <h1>Video Games</h1>
            <button onClick ={ e=>{handleClick(e)}}>
              volver a cargar los personajes
            </button>

            <div>


            <label>Sort By:</label>
            <select onChange={(e) => handleRatingSort(e)}>
              <option hidden value="Rating">
                Rating
              </option>
              <option value="Highest to Lowest Rating">
                Highest to Lowest
              </option>
              <option value="Lowest to Highest Rating">
                Lowest to Highest
              </option>
            </select>

            <select onChange={(e) => handleSort(e)}>
              <option hidden value="Alphabetically">
                Alphabetically
              </option>
              <option value="A - Z">A - Z</option>
              <option value="Z - A">Z - A</option>
            </select>









                <select>
                    <option value="all">todos</option>
                    <option value="created">creados</option>
                    <option value="api">existente</option>
                </select>

              <div>
                <Paginado
                gamesPerPage={gamesPerPage}
                allGames={allGames.length}
                paginado={paginado}/>
                </div>




                {currentGames?.map((c)=>{
                    return(
                        <div className="cards" key={c.id}>
                            {/*Link to={"/home/" + c.id}*/}
                                <Card 
                                name={c.name} 
                                image={c.image} 
                                genres={c.genero} 
                                rating={c.rating}
                                id={c.id}/>
                            {/*/Link>*/}
                        </div>
                    )
                })}


            </div>
        </div>
        )






}
