import{SORT_RATING,SORT_ALPHABETICALLY} from"../actions/index"


const initialState = {
    games :[]
}

function rootReducer (state = initialState,action) {
     switch (action.type){
        case "GET_GAMES":
            return{
                ...state,
                games:action.payload
            }
             case SORT_RATING:
                let sortRating = action.payload === 'Lowest to Highest Rating' ?
                state.games.sort((a,b) => {
                    if(a.rating > b.rating){
                        return 1;
                    }
                    if(a.rating < b.rating){
                        return -1;
                    }
                    return 0;
                }) : 
                state.games.sort((a,b) => {
                    if(a.rating > b.rating){
                        return -1;
                    }
                    if(a.rating < b.rating){
                        return 1;
                    }
                    return 0;
                })
                return {
                    ...state,
                    games: sortRating
                }


            case SORT_ALPHABETICALLY:
                    let sort = action.payload === 'A - Z' ?
                    state.games.sort((a,b) => {
                    if(a.name > b.name){
                    return 1;
                    }
                     if(a.name < b.name){
                    return -1;
                    }
                    return 0;
                    }) :  state.games.sort((a,b) => {
                    if(a.name > b.name){
                    return -1;
                    }
                    if(a.name < b.name){
                    return 1;
                    }
                    return 0;
                    })
                    return {
                    ...state,
                    games: sort
                    }





            default:
                return state;
     }


}

export default rootReducer
