import{SORT_RATING,SORT_ALPHABETICALLY,FILTER_GAMES_GENRE} from"../actions/index"


const initialState = {
    games :[]
}

function rootReducer (state = initialState,action) {
     switch (action.type){
        case "GET_GAMES":
            return{
                ...state,
                games:action.payload
            }
             case SORT_RATING:
                let sortRating = action.payload === 'Lowest to Highest Rating' ?
                state.games.sort((a,b) => {
                    if(a.rating > b.rating){
                        return 1;
                    }
                    if(a.rating < b.rating){
                        return -1;
                    }
                    return 0;
                }) : 
                state.games.sort((a,b) => {
                    if(a.rating > b.rating){
                        return -1;
                    }
                    if(a.rating < b.rating){
                        return 1;
                    }
                    return 0;
                })
                return {
                      ...state,
                     games: sortRating
                         }


            case SORT_ALPHABETICALLY:
                    let sort = action.payload === 'A - Z' ?
                    state.games.sort((a,b) => {
                    if(a.name > b.name){
                    return 1;
                    }
                     if(a.name < b.name){
                    return -1;
                    }
                    return 0;
                    }) :  state.games.sort((a,b) => {
                    if(a.name > b.name){
                    return -1;
                    }
                    if(a.name < b.name){
                    return 1;
                    }
                    return 0;
                    })
                    return {
                       ...state,
                       games: sort
                             }


            case FILTER_GAMES_GENRE:
                    const allGames = state.allGames
                    const gamesApi = allGames.filter(el => el.genres.includes(action.payload))
                    const gamesDb = allGames.filter((el) => {
                    for(let i = 0; i < el.genres.length; i++){
                    if(el.genres[i].name === action.payload){
                        return el
                       }
                    }
                     })
                    const allFiltered = gamesApi.concat(gamesDb)
                        return{
                        ...state,
                        games: allFiltered
                               }



            default:
                return state;
     }


}

export default rootReducer
